import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as d,c as n,b as e,d as o,a as i,e as s}from"./app-21d4bb55.js";const r="/assets/1-1694333207877-49-17b3c5e2.png",p="/assets/2-1694333213723-51-e0a36db4.png",l="/assets/3-1694333219219-53-41da7d09.png",g="/assets/4-1694333224389-55-bab520be.png",_="/assets/5-1694333236240-57-ad8fd8bc.png",f="/assets/6-1694333245870-59-c14fe477.png",u="/assets/7-1694333250580-61-272728ee.png",h={},m=e("h2",{id:"dvwa介绍",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#dvwa介绍","aria-hidden":"true"},"#"),o(" DVWA介绍")],-1),b=e("p",null,"DVWA（Damn Vulnerable Web Application）是一个用来进行安全脆弱性鉴定的PHP/MySQL Web 应用，旨在为安全专业人员测试自己的专业技能和工具提供合法的环境，帮助web开发者更好的理解web应用安全防范的过程。",-1),S=e("p",null,"DVWA 一共包含了十个攻击模块，分别是：Brute Force（暴力（破解））、Command Injection（命令行注入）、CSRF（跨站请求伪造）、- File Inclusion（文件包含）、File Upload（文件上传）、Insecure CAPTCHA （不安全的验证码）、SQL Injection（SQL注入）、SQL Injection（Blind）（SQL盲注）、XSS（Reflected）（反射型跨站脚本）、XSS（Stored）（存储型跨站脚本）。包含了 OWASP TOP10 的所有攻击漏洞的练习环境，一站式解决所有 Web 渗透的学习环境。",-1),W=e("p",null,"另外，DVWA 还可以手动调整靶机源码的安全级别，分别为 Low，Medium，High，Impossible，级别越高，安全防护越严格，渗透难度越大。一般 Low 级别基本没有做防护或者只是最简单的防护，很容易就能够渗透成功；而 Medium 会使用到一些非常粗糙的防护，需要使用者懂得如何去绕过防护措施；High 级别的防护则会大大提高防护级别，一般 High 级别的防护需要经验非常丰富才能成功渗透；最后 Impossible 基本是不可能渗透成功的，所以 Impossible 的源码一般可以被参考作为生产环境 Web 防护的最佳手段",-1),P=e("h2",{id:"准备工作",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#准备工作","aria-hidden":"true"},"#"),o(" 准备工作")],-1),y=e("p",null,[o("这里直接在自己的电脑上进行部署了，方便一些；DVWA 是"),e("code",null,"PHP/MySQL"),o("的源码环境，所以需要准备PHP和MySQL的运行环境。PHPStudy是一个PHP调试环境的程序集成包。")],-1),A={href:"https://www.xp.cn/download.html",target:"_blank",rel:"noopener noreferrer"},V={href:"https://github.com/ethicalhack3r/DVWA/archive/master.zip",target:"_blank",rel:"noopener noreferrer"},D=s('<h2 id="安装phpstudy和dvwa" tabindex="-1"><a class="header-anchor" href="#安装phpstudy和dvwa" aria-hidden="true">#</a> 安装PHPStudy和DVWA</h2><p>PHPStudy直接正常安装即可，建议放在除了C盘之外的盘，路径不要有中文字符就好，然后直接启动，启动<code>Apache</code>和<code>MySQL</code>即可。</p><figure><img src="'+r+'" alt="1" tabindex="0" loading="lazy"><figcaption>1</figcaption></figure><p>对于DVWA，直接解压即可，然后把解压的文件夹名称改为<code>DVWA</code>，把这个文件夹复制到<code>PHPStudy</code>默认网站的目录下：<code>D:\\phpstudy_pro\\WWW\\</code>，然后进入<code>DVWA\\config\\</code>文件夹，把<code>config.inc.php.dist</code>文件，重命名为<code>config.inc.php</code></p><figure><img src="'+p+'" alt="2" tabindex="0" loading="lazy"><figcaption>2</figcaption></figure><p>用记事本打开<code>config.inc.php</code>文件，把配置文档中的<code>$_DVWA[ &#39;db_password&#39; ] = &#39;P@ssw0rd&#39;;</code>修改为 <code>$_DVWA[ &#39;db_password&#39; ] = &#39;root&#39;;</code>，这里修改的是MySQL的管理员密码，该密码默认是root，如果修改过MySQL密码，这里需要改成你自己的密码</p><figure><img src="'+l+'" alt="3" tabindex="0" loading="lazy"><figcaption>3</figcaption></figure><p>随后可以先使用<code>ipconfig</code>查看自己的IP地址，比如<code>192.168.124.11</code>，然后访问<code>192.168.124.11/dvwa/setup.php</code>即可</p><figure><img src="'+g+'" alt="查看自己的IP" tabindex="0" loading="lazy"><figcaption>查看自己的IP</figcaption></figure><figure><img src="'+_+'" alt="安装界面" tabindex="0" loading="lazy"><figcaption>安装界面</figcaption></figure><p>在安装页面下方点击<code>Creat / Reset Database</code>按钮来创建数据库，出现提示<code>Setup Success</code>则说明安装成功；如果此处创建失败，检查上一步<code>MySQL密码</code>是否正确</p><figure><img src="'+f+'" alt="6" tabindex="0" loading="lazy"><figcaption>6</figcaption></figure><p>然后点击<code>login</code>，进入登录页面，使用默认用户<code>admin</code>，密码<code>password</code>登录到渗透练习页。</p><p>点击<code>DVWA Security</code>按钮，在这个页面中可以设置DVWA的安全级别，可以发现当前默认的安全级别为<code>Impossible</code></p><figure><img src="'+u+'" alt="7" tabindex="0" loading="lazy"><figcaption>7</figcaption></figure><p>然后就安装完啦~撒花</p>',16);function w(x,L){const c=a("ExternalLinkIcon");return d(),n("div",null,[m,b,S,W,P,y,e("ul",null,[e("li",null,[e("a",A,[o("PhpStudy官网"),i(c)])]),e("li",null,[e("a",V,[o("DVWA下载链接"),i(c)])])]),D])}const Q=t(h,[["render",w],["__file","DVWA的安装.html.vue"]]);export{Q as default};
